targets.expr:
(label_replace(
    purefa_volume_performance_throughput_iops{
      os_region=~"$cluster",
      dimension="writes_per_sec"
    },
    "id", "$1", "name", "volume-(.*)-cinder"
  )
  + on(id) group_left(volume_type, volume_name)
  (
    0 * (label_replace(
      openstack_cinder_volume_gb{
        os_region=~"$cluster",
        volume_type=~".*-${iops_limit}$"
      },
      "volume_name", "$1", "name", "(.*)"
      )
    )
  )
) > (${iops_limit} * 0.85)

(label_replace(
    purefa_volume_performance_throughput_iops{
      os_region=~"$cluster",
      dimension="reads_per_sec"
    },
    "id", "$1", "name", "volume-(.*)-cinder"
  )
  + on(id) group_left(volume_type, volume_name)
  (
    0 * (label_replace(
      openstack_cinder_volume_gb{
        os_region=~"$cluster",
        volume_type=~".*-${iops_limit}$"
      },
      "volume_name", "$1", "name", "(.*)"
      )
    )
  )
) > (${iops_limit} * 0.90)
