targets.expr:
(
  sum by (host_name, vmware_cluster_name, dc_name)(vmware_host_maintenance_mode{vmware_cluster_name=~"$cluster"})
  +
  on (host_name) group_left(hardware_cpu_model, hardware_model)
  0 * vmware_host_hardware_info{vmware_cluster_name=~"$cluster"}
)
+
on (host_name) group_left(build, version)
0 * vmware_host_product_info{vmware_cluster_name=~"$cluster"}
