targets.expr:
sum by (hour) (
  count_over_time(
    ((vmware2openstack_migration_status{site="${site}"} == 13 or vmware2openstack_migration_status{site="${site}"} == 14) and 
     vmware2openstack_migrated_at{site="${site}"} > time() - 86400)  # Last 24 hours (fixed)
    * on (vm_name) group_left(os,service,team) vmware2openstack_vm_metadata{site="$site"}
    )
) or vector(0)

sum by (hour) (
  count_over_time(
    ((vmware2openstack_migration_status{site="$site"} == 13 or vmware2openstack_migration_status{site="$site"} == 14) and 
     vmware2openstack_cutover_at{site="$site"} > time() - 86400)  # Last 24 hours (fixed)
    * on (vm_name) group_left(os,service,team) vmware2openstack_vm_metadata{site="$site"}
    )
)

sum(
  libvirt_domain_info_maximum_memory_bytes{job=~"libvirt_exporter"} * on(domain) group_left(flavor,instance_name,project_name,user_name) 
  (
    libvirt_domain_info_meta{job=~"libvirt_exporter",os_region=~"$cluster",project_name="$project"} or
    libvirt_domain_openstack_info{job=~"libvirt_exporter",os_region=~"$cluster",project_name="$project"}
  )
) - sum(
  libvirt_domain_memory_stats_unused_bytes{job=~"libvirt_exporter"} * on(domain) group_left(flavor,instance_name,project_name,user_name) 
  (
    libvirt_domain_info_meta{job=~"libvirt_exporter",os_region="$cluster",project_name="$project"} or
    libvirt_domain_openstack_info{job=~"libvirt_exporter",os_region="$cluster",project_name="$project"}
  )
) + (2.5*sum(openstack_placement_resource_reserved{os_region=~"$cluster",resourcetype="MEMORY_MB"})*1024*1024)
